import {SafeAreaView, Text, Image, TouchableOpacity, View,StyleSheet,Button,Alert,Dimensions} from 'react-native';
import React, { useEffect, useState, useRef } from "react";
import { getStorage, ref, uploadBytes, getDownloadURL } from "firebase/storage";
import {Logo,CameraIcon,BottomLayer,LeftArrow,HomeIcon,ProfileImage,BottomBar,ContinueButton} from '../components/Svgs';
import FontAwesome5Icon from "react-native-vector-icons/FontAwesome5";
import FontAwesome5 from "react-native-vector-icons/FontAwesome5";
import { Camera } from "expo-camera";
import { manipulateAsync } from "expo-image-manipulator";

import uuid from "uuid";
import { getReceiptInfo } from "../backendFiles/GoogleVision";
import { extractData } from "../backendFiles/TextParser";
import { useNavigation } from "@react-navigation/native";



const height = Dimensions.get('window').height;
const width = Dimensions.get('window').width;

const ScannedSlip = () => {
  const navigation = useNavigation();
  const [isLoading, setisLoading] = useState(false);
  const [hasCameraPermission, setHasCameraPermission] = useState(null);
  const [TextReaded, setImageUrl] = useState("");
  const [ImageUrl, setTextReaded] = useState("");

  const [type, setType] = useState(Camera.Constants.Type.back);
  const [capturedImage, setCapturedImage] = useState(null);
  const cameraRef = useRef(null);
  var takingPicture = false;

  const takePicture = async () => {
    if(!takingPicture){
      takingPicture = true;
      if (cameraRef.current) {
        console.log("Took a picture!");
        const data = await cameraRef.current.takePictureAsync();
        const manipResult = await manipulateAsync(
          data?.uri,
          [{ resize: { width: 800, height: 1200 } }],
          { format: "jpeg", base64: true }
        );
        setCapturedImage(manipResult.uri);
        takingPicture = false;
        // if (manipResult.uri != undefined) {
        //   const firebaseURL = await uploadImageAsync(manipResult.uri);
        //   console.log(firebaseURL);
        //   var imageData = await getReceiptInfo(firebaseURL);
        //   console.log(imageData, "/////");
        //   var extractedData = await extractData(imageData);
        //   console.log(imageData[0],'/....');
        //   if (extractedData) {
        //     // navigation?.navigate("Calculatingsummary", {
        //     //   item: route?.params,
        //     //   ScannedText: extractedData,
        //     //   title:imageData[0]
        //     // });
        //   }
        // }
      }
    }



  };

  async function uploadImageAsync(uri) {
    const blob = await new Promise((resolve, reject) => {
      const xhr = new XMLHttpRequest();
      xhr.onload = function () {
        resolve(xhr.response);
      };
      xhr.onerror = function (e) {
        console.log(e);
        reject(new TypeError("Network request failed"));
      };
      xhr.responseType = "blob";
      xhr.open("GET", uri, true);
      xhr.send(null);
    });
    const fileRef = ref(getStorage(), uuid.v4());
    const result = await uploadBytes(fileRef, blob);
    blob.close();
    return await getDownloadURL(fileRef);
  }

  const resetCamera = async () => {
    setCapturedImage(undefined);
  };

  const finishReceipt = async () => {
    navigation.navigate("CreateTransaction");
  };

  return (
    <SafeAreaView style ={{height:1000,}}>
      <Logo/>



        <BottomLayer/>
        <BottomBar/>

        {capturedImage!=undefined ? (
            <TouchableOpacity onPress={() => finishReceipt()}>
              <ContinueButton/>
            </TouchableOpacity>
        ) : (
            <TouchableOpacity onPress={() => takePicture()}>
              <CameraIcon/>
            </TouchableOpacity>
        )}

        <View>
          <Text style={styles.myGroup}>Add expense</Text>
        </View>



        {capturedImage!=undefined && 
            <TouchableOpacity style={styles.retakePicture} onPress={() => resetCamera()}>
                <FontAwesome5
                          name={"redo"}
                          color={"#9E9E9E"}
                          size={30}
                />
            </TouchableOpacity>
         }
        <TouchableOpacity onPress={() => navigation.goBack()}>
            <LeftArrow style={{ right: 100 }} />
          </TouchableOpacity>

        <View style={styles.image}>
            <Camera ref={cameraRef} style={styles.camera} type={type}></Camera>
        </View>


        {capturedImage!=undefined && 
          <Image
            source={{
              uri: capturedImage,
            }}
            style={styles.image}
          />
         }


    </SafeAreaView>
  );
};

export default ScannedSlip;

const styles = StyleSheet.create({
  myGroup: { color: "black", fontWeight: "bold", left: 40, fontSize: 20,top:60},
  PlusButton: {
    height: 80,
    width: 80,
    top:630,
    position:'absolute',
    borderWidth: 3,
    justifyContent: "center",
    alignItems: "center",
    borderColor: "#4461F2",
    alignSelf: "center",
    shadowColor: "#4461F2",
    shadowOffset: { width: 2, height: 2 },
    shadowOpacity: 0.9,
    shadowRadius: 4,
  },
  retakePicture: {
    height: 80,
    width: 80,
    top:60,
    position:'absolute',
    alignSelf:'flex-end'
  },
  MyGroupSpace: {
    top:200,
    height:400,
    width:300,
    backgroundColor: "#EAF0F7",
    alignSelf: "center",
    position:'absolute',
  },
  camera: {
    flex: 1,
    height: "100%",
    width: "100%",
  },
  picture:{
    top:130,
    alignSelf: "center",
    position:'absolute',
    justifyContent: "center",
    alignItems: "center",
    height:400,
    width:300,
    backgroundColor: "#EAF0F7",
  },
  image:{
    position:'absolute',
    alignSelf:'center',
    top:140,
    width:300,
    height:500,
  },
});


